func:
/*
* Audio Tool - Play pre-recorded prompts and messages
* You can use properties: $audioType, $language, $customMessage
* Must return a string value at the end of function
*/

// Audio prompt library
const audioPrompts = {
  welcomeBack: {
    en: 'Welcome back! I\'m glad to help you today.',
    es: '¡Bienvenido de nuevo! Me alegra ayudarte hoy.'
  },
  newPatientDisclaimer: {
    en: 'Thank you for choosing our practice. Before we proceed, please note that we specialize in pediatric dental care for patients 16 years and under. If you are 17 or older, we can help you find appropriate care. Let\'s continue with your appointment.',
    es: 'Gracias por elegir nuestra práctica. Antes de continuar, tenga en cuenta que nos especializamos en atención dental pediátrica para pacientes de 16 años o menos. Si tiene 17 años o más, podemos ayudarle a encontrar la atención adecuada. Continuemos con su cita.'
  },
  appointmentConfirmed: {
    en: 'Your appointment has been confirmed. A text reminder will be sent to you shortly.',
    es: 'Su cita ha sido confirmada. Se le enviará un recordatorio por mensaje de texto en breve.'
  },
  appointmentCancelled: {
    en: 'Your appointment has been cancelled. If you need to reschedule, please call us back.',
    es: 'Su cita ha sido cancelada. Si necesita reprogramar, por favor llámenos de nuevo.'
  },
  seeYouSoon: {
    en: 'See you soon! Have a great day!',
    es: '¡Nos vemos pronto! ¡Que tenga un gran día!'
  },
  transferMessage: {
    en: 'Please hold while I transfer you to someone who can help.',
    es: 'Por favor espere mientras lo transfiero a alguien que pueda ayudarle.'
  }
};

try {
  const audioType = $audioType;
  const language = ($language || 'en').toLowerCase();
  const customMessage = $customMessage;

  // Validate language
  if (language !== 'en' && language !== 'es') {
    return JSON.stringify({
      success: false,
      error: 'Invalid language',
      message: 'Language must be \'en\' (English) or \'es\' (Spanish)'
    });
  }

  // Use custom message if provided, otherwise use audio type
  let messageToPlay;

  if (customMessage) {
    messageToPlay = customMessage;
  } else if (audioType && audioPrompts[audioType]) {
    messageToPlay = audioPrompts[audioType][language] || audioPrompts[audioType]['en'];
  } else {
    return JSON.stringify({
      success: false,
      error: 'Invalid audio type or missing custom message',
      message: `Audio type '${audioType}' not found. Valid types: ${Object.keys(audioPrompts).join(', ')}`,
      availableTypes: Object.keys(audioPrompts)
    });
  }

  // Simulate audio playback
  // In real system, this would trigger actual audio playback
  const playbackResult = {
    audioType: customMessage ? 'custom' : audioType,
    language: language,
    message: messageToPlay,
    playbackStarted: true,
    timestamp: new Date().toISOString()
  };

  return JSON.stringify({
    success: true,
    operation: 'playAudio',
    result: playbackResult,
    message: `Audio playback initiated: ${messageToPlay.substring(0, 50)}...`
  }, null, 2);

} catch (error) {
  console.error('Audio tool error:', error);
  return JSON.stringify({
    success: false,
    error: error.message,
    message: 'Audio playback temporarily unavailable. Please try again.'
  });
}
