const fetch = require('node-fetch');
const operatoryIds = $operatoryIds;
let days = null;
let providerId = null;
let appointmentTypeId = null;
try { days = $days || '5'; } catch(e) { days = '5'; }
try { providerId = $providerId || null; } catch(e) { providerId = null; }
try { appointmentTypeId = $appointmentTypeId || null; } catch(e) { appointmentTypeId = null; }

const apiKey = `dXNlci0yNzQtcHJvZHVjdGlvbg.eqAjEe1KyadP0pZ56bNbULBObZG3VKqU`;

const myHeaders = {
    "Accept": "application/vnd.Nexhealth+json;version=2",
    "Authorization": apiKey
};

const requestOptions = {
    method: "POST",
    headers: myHeaders,
    redirect: "follow"
};

// First, authenticate to get the token
fetch("https://nexhealth.info/authenticates", requestOptions)
    .then((response) => response.json())
    .then((authResult) => {
        console.log('Auth response:', authResult);
        const authToken = authResult.data.token;
        console.log('Auth Token:', authToken);

        // Build query parameters for appointment_slots endpoint
        const operatoryIdArray = operatoryIds.split(',').map(id => id.trim());
        let queryParams = "subdomain=cdm&days=" + (days || '5');

        // Add operatory_ids as array parameters
        operatoryIdArray.forEach(id => {
            queryParams += `&operatory_ids[]=${id}`;
        });

        if (providerId) {
            queryParams += `&provider_id=${providerId}`;
        }
        if (appointmentTypeId) {
            queryParams += `&appointment_type_id=${appointmentTypeId}`;
        }

        // Now get available appointment slots with the auth token
        const availabilityHeaders = {
            "Accept": "application/vnd.Nexhealth+json;version=2",
            "Authorization": "Bearer " + authToken
        };

        const availabilityRequestOptions = {
            method: "GET",
            headers: availabilityHeaders,
            redirect: "follow"
        };

        return fetch(`https://nexhealth.info/appointment_slots?${queryParams}`, availabilityRequestOptions);
    })
    .then((response) => response.json())
    .then((result) => {
        console.log('Available appointment slots:', result);
        const slotsResult = result;
        console.log(slotsResult);
        return slotsResult;
    })
    .catch((error) => console.error('Error:', error));
