const fetch = require('node-fetch');
const firstName = $firstName;
const lastName = $lastName;
const dateOfBirth = $dateOfBirth;
const phoneNumber = $phoneNumber;
let email = null;
let address = null;
try { email = $email || null; } catch(e) { email = null; }
try { address = $address || null; } catch(e) { address = null; }

const apiKey = `dXNlci0yNzQtcHJvZHVjdGlvbg.eqAjEe1KyadP0pZ56bNbULBObZG3VKqU`;

const myHeaders = {
    "Accept": "application/vnd.Nexhealth+json;version=2",
    "Authorization": apiKey
};

const requestOptions = {
    method: "POST",
    headers: myHeaders,
    redirect: "follow"
};

// First, authenticate to get the token
fetch("https://nexhealth.info/authenticates", requestOptions)
    .then((response) => response.json())
    .then((authResult) => {
        console.log('Auth response:', authResult);
        const authToken = authResult.data.token;
        console.log('Auth Token:', authToken);

        // Now create the patient with the auth token
        const patientHeaders = {
            "Content-Type": "application/json",
            "Accept": "application/vnd.Nexhealth+json;version=2",
            "Authorization": "Bearer " + authToken
        };

        const patientData = {
            "patient": {
                "first_name": firstName,
                "last_name": lastName,
                "date_of_birth": dateOfBirth,
                "phone": phoneNumber
            }
        };

        if (email) {
            patientData.patient.email = email;
        }
        if (address) {
            patientData.patient.address = address;
        }

        const raw = JSON.stringify(patientData);

        const patientRequestOptions = {
            method: "POST",
            headers: patientHeaders,
            body: raw,
            redirect: "follow"
        };

        return fetch("https://nexhealth.info/patients?subdomain=cdm&location_id=333725", patientRequestOptions);
    })
    .then((response) => response.json())
    .then((result) => {
        console.log('Patient created:', result);
        const patientResult = result;
        console.log(patientResult);
        return patientResult;
    })
    .catch((error) => console.error('Error:', error));
