const fetch = require('node-fetch');
const appointmentId = $appointmentId;

const apiKey = `dXNlci0yNzQtcHJvZHVjdGlvbg.eqAjEe1KyadP0pZ56bNbULBObZG3VKqU`;

const myHeaders = {
    "Accept": "application/vnd.Nexhealth+json;version=2",
    "Authorization": apiKey
};

const requestOptions = {
    method: "POST",
    headers: myHeaders,
    redirect: "follow"
};

// First, authenticate to get the token
return fetch("https://nexhealth.info/authenticates", requestOptions)
    .then((response) => response.json())
    .then((authResult) => {
        console.log('Auth response:', authResult);
        const authToken = authResult.data.token;
        console.log('Auth Token:', authToken);

        // Now confirm the appointment with the auth token
        const apptHeaders = {
            "Content-Type": "application/json",
            "Accept": "application/vnd.Nexhealth+json;version=2",
            "Authorization": "Bearer " + authToken
        };

        const raw = JSON.stringify({
            "appt": {
                "confirmed": true
            }
        });

        const apptRequestOptions = {
            method: "PATCH",
            headers: apptHeaders,
            body: raw,
            redirect: "follow"
        };

        return fetch(`https://nexhealth.info/appointments/${appointmentId}?subdomain=cdm&id=${appointmentId}`, apptRequestOptions);
    })
    .then((response) => response.json())
    .then((result) => {
        console.log('Appointment confirmed:', result);
        const apptResult = result;
        console.log(apptResult);
        return apptResult;
    })
    .catch((error) => {
        console.error('Error:', error);
        throw error;
    });
