const fetch = require('node-fetch');
const startTime = $appointmentTime;
const patientId = $patientId;
const providerId = $providerId;
const operatoryId = $operatoryId;
const note = $note || "Schedule by AI";

const apiKey = `dXNlci0yNzQtcHJvZHVjdGlvbg.eqAjEe1KyadP0pZ56bNbULBObZG3VKqU`;

let myHeaders = new Headers();
myHeaders.append("Accept", "application/vnd.Nexhealth+json;version=2");
myHeaders.append("Authorization", apiKey);

const requestOptions = {
    method: "POST",
    headers: myHeaders,
    redirect: "follow"
};

// First, authenticate to get the token
fetch("https://nexhealth.info/authenticates", requestOptions)
    .then((response) => response.json())
    .then((authResult) => {
        console.log('Auth response:', authResult);
        const authToken = authResult.data.token;
        console.log('Auth Token:', authToken);

        // Now create the appointment with the auth token
        const apptHeaders = new Headers();
        apptHeaders.append("Content-Type", "application/json");
        apptHeaders.append("Accept", "application/vnd.Nexhealth+json;version=2");
        apptHeaders.append("Authorization", "Bearer " + authToken);

        const raw = JSON.stringify({
            "appt": {
                "patient_id": parseInt(patientId),
                "provider_id": parseInt(providerId),
                "start_time": startTime,
                "operatory_id": parseInt(operatoryId),
                "note": note
            },
            "appointments_per_timeslot": 1
        });

        const apptRequestOptions = {
            method: "POST",
            headers: apptHeaders,
            body: raw,
            redirect: "follow"
        };

        return fetch("https://nexhealth.info/appointments?subdomain=cdm&location_id=333724&notify_patient=false", apptRequestOptions);
    })
    .then((response) => response.json())
    .then((result) => {
        console.log('Appointment created:', result);
        const apptResult = result;
        console.log(apptResult);
        return apptResult;
    })
    .catch((error) => console.error('Error:', error));
