const fetch = require('node-fetch');
const providerId = $providerId;
const startDate = $startDate;
const endDate = $endDate;
const appointmentTypeId = $appointmentTypeId;

const apiKey = `dXNlci0yNzQtcHJvZHVjdGlvbg.eqAjEe1KyadP0pZ56bNbULBObZG3VKqU`;

let myHeaders = new Headers();
myHeaders.append("Accept", "application/vnd.Nexhealth+json;version=2");
myHeaders.append("Authorization", apiKey);

const requestOptions = {
    method: "POST",
    headers: myHeaders,
    redirect: "follow"
};

// First, authenticate to get the token
fetch("https://nexhealth.info/authenticates", requestOptions)
    .then((response) => response.json())
    .then((authResult) => {
        console.log('Auth response:', authResult);
        const authToken = authResult.data.token;
        console.log('Auth Token:', authToken);

        // Build query parameters for availability endpoint
        let queryParams = "subdomain=cdm&location_id=333724";
        if (providerId) {
            queryParams += `&provider_id=${providerId}`;
        }
        if (startDate) {
            queryParams += `&start_date=${startDate}`;
        }
        if (endDate) {
            queryParams += `&end_date=${endDate}`;
        }
        if (appointmentTypeId) {
            queryParams += `&appointment_type_id=${appointmentTypeId}`;
        }

        // Now get available appointment slots with the auth token
        const availabilityHeaders = new Headers();
        availabilityHeaders.append("Accept", "application/vnd.Nexhealth+json;version=2");
        availabilityHeaders.append("Authorization", "Bearer " + authToken);

        const availabilityRequestOptions = {
            method: "GET",
            headers: availabilityHeaders,
            redirect: "follow"
        };

        return fetch(`https://nexhealth.info/availability?${queryParams}`, availabilityRequestOptions);
    })
    .then((response) => response.json())
    .then((result) => {
        console.log('Available appointment slots:', result);
        const slotsResult = result;
        console.log(slotsResult);
        return slotsResult;
    })
    .catch((error) => console.error('Error:', error));
